package a11;import java.io.DataInputStream;import java.io.DataOutputStream;import java.io.EOFException;import java.io.IOException;import java.net.InetAddress;import java.net.Socket;import java.net.UnknownHostException;import java.util.Scanner;public class TCPClient {	public static void main(String args[]) {		try {			int serverPort = 12345;			Socket s = new Socket(InetAddress.getByName(null), serverPort);			System.out.println(s.toString());			DataInputStream in = new DataInputStream(s.getInputStream());			DataOutputStream out = new DataOutputStream(s.getOutputStream());						Scanner sc = new Scanner(System.in);			String line = sc.next();						out.writeUTF(line);			String data = in.readUTF();			System.out.println("Received: " + data);			s.close();		} catch (UnknownHostException e) {			System.out.println("UnknownHostException: ");			e.printStackTrace();		} catch (EOFException e) {			System.out.println("EOFException: ");			e.printStackTrace();		} catch (IOException e) {			System.out.println("IOException: ");			e.printStackTrace();		}	}}