package a11;import java.io.DataInputStream;import java.io.DataOutputStream;import java.io.EOFException;import java.io.IOException;import java.net.InetAddress;import java.net.Socket;import java.net.UnknownHostException;import java.util.Scanner;/** * Einfacher TCPClient für unseren TCPServer *  * @author Martin Slowikowski * @author Jan-Tristan Rudat * @version 1.0beta */public class TCPClient {	/**	 * Einfacher Client, sendet Text an den Server und mirrort die Antwort auf der Konsole.	 * @param args not needed, man könnte hier gezielt wieder über [0] IP des Servers (wenn nicht auf	 * dem selben PC) und unter [1] den Serverport übergeben...	 */	public static void main(String args[]) {		try {			int serverPort = 12345;			Socket s = new Socket(InetAddress.getByName(null), serverPort);			System.out.println(s.toString());			DataInputStream in = new DataInputStream(s.getInputStream());			DataOutputStream out = new DataOutputStream(s.getOutputStream());						Scanner sc = new Scanner(System.in);			String line = sc.next();						out.writeUTF(line);			String data = in.readUTF();			System.out.println("Received: " + data);			s.close();		} catch (UnknownHostException e) {			System.out.println("UnknownHostException: ");			e.printStackTrace();		} catch (EOFException e) {			System.out.println("EOFException: ");			e.printStackTrace();		} catch (IOException e) {			System.out.println("IOException: ");			e.printStackTrace();		}	}}